-- вытащить из Битрикс ФИО+Мейл+тел+статус для списка заказов

SET NAMES 'utf8';

SELECT
    `order`.`ADDITIONAL_INFO` as `ORDER_NUM`,
    CONCAT(`user`.`LAST_NAME`,`user`.`NAME`) as `FIO`,
    `user`.`EMAIL` AS `EMAIL`,
    RIGHT(`user`.`LOGIN`,11) as `PHONE`,
    `status`.`NAME` as `STATUS`
FROM
    `b_sale_order` as `order`
        LEFT JOIN
    `b_user` as `user`
        ON `order`.`USER_ID`=`user`.`ID`
        LEFT JOIN
    `b_sale_status_lang` as `status`
        ON `order`.`status_id`=`status`.`STATUS_ID` AND `status`.`LID`='ru'
